import 'package:flutter/material.dart';
import 'package:zapateria/InformacionProducto.dart';

// Definici√≥n de una clase Producto
class Producto {
  String nombre;
  String imageUrl;

  Producto({required this.nombre, required this.imageUrl});
}

class ProductScreen extends StatelessWidget {
  const ProductScreen({super.key});
  
  final List<Producto> productos = [
    Producto(nombre: "Botines ", imageUrl: "data:image/jpeg;base64,"),
    Producto(nombre: "Tacos", imageUrl: "data:image/jpeg;base64,"),
    Producto(nombre: "Zapatos", imageUrl: "hdata:image/jpeg;base64,"),
    Producto(nombre: "Botines 4", imageUrl: "URL de la imagen 4"),
  ];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text('Productos'),
        actions: [
          IconButton(
            icon: Icon(Icons.arrow_back),
            onPressed: () {
              Navigator.pop(context);
            },
          ),
        ],
      ),
      body: Column(
        children: [
          Padding(
            padding: const EdgeInsets.all(16.0),
            child: TextField(
              decoration: InputDecoration(
                hintText: 'Buscar...',
                prefixIcon: Icon(Icons.search),
                border: OutlineInputBorder(),
              ),
            ),
          ),
          SizedBox(height: 20),
          Expanded(
            child: ListView.builder(
              itemCount: productos.length,
              itemBuilder: (context, index) {
                // Obtener el producto actual
                Producto producto = productos[index];

                return GestureDetector(
                  onTap: () {
                    // Navegar a la p√°gina de detalles del producto cuando se toca la imagen
                    Navigator.push(
                      context,
                      MaterialPageRoute(
                          builder: (_) => InformacionProductoScreen()),
                    );
                  },
                  child: Container(
                    margin: EdgeInsets.all(10),
                    child: Column(
                      children: [
                        Image.network(
                          producto.imageUrl,
                          height: 120,
                          width: 120,
                          // A√±adir el nombre del producto como texto debajo de la imagen
                          errorBuilder: (context, error, stackTrace) {
                            return Text('Error al cargar la imagen');
                          },
                        ),
                        SizedBox(height: 8),
                        Text(producto.nombre),
                        SizedBox(height: 8),
                        Text(
                          "‚≠ê‚≠ê‚≠ê‚≠ê",
                          style: TextStyle(fontSize: 9),
                        ),
                      ],
                    ),
                    height: 200,
                    width: 200,
                    decoration: BoxDecoration(
                      border:
                          Border.all(color: Color.fromARGB(211, 172, 12, 212)),
                    ),
                  ),
                );
              },
            ),
          ),
        ],
      ),
    );
  }
}
